<?phpinclude_once('config/db.php');set_time_limit(0);ini_set('memory_limit', '3000M');$action = $_POST['act'];if($action == "action1"){    $db = new DB();    $query = $_POST['query'];      $data = $db->selectQuery($query);    if(is_array($data))    {        $datenow = date("m-d_H-i-s_");        $file_name = $datenow . 'test.txt';        $file_path = 'file/'. $file_name;        $file = fopen($file_path,"w");                $element_first = reset($data);        writeFile($file, $element_first, 'key');                foreach($data as $key=>$value)        {            writeFile($file, $value);        }        fclose($file);        $link_download = BASE_URL . $file_path;        $result = array(                'name_file' => $file_name,                'link_file' => $link_download        );        echo json_encode($result);    }    else    {        echo $data;    }}function writeFile($file = null, $array, $st=""){    if($file == null) return;    $i = 1;    foreach($array as $key=>$value)    {        if($st == "")            fwrite($file,$value);        else            fwrite($file,$key);                if($i != count($array))fwrite($file,"\t");        $i++;    }    fwrite($file,"\r\n");}